// switch case
#include <iostream>
using namespace std;
    int main(){
        int a;
        cout << "typing here chose betwen 1-5: ";
        cin >> a;
        switch (a)
        {
        case 1:
            cout << "you select number 1";
            break;
        case 2:
            cout << "you select number 2";
            break;
        case 3:
            cout << "you select number 3";
            break;
        case 4:
            cout << "you select number 4";
            break;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
        default:
            cout << "Chose betwen 1-5";
            break;
        }
        cout << endl;
        cout << "end the program";
        cin.get();
        return 0;
    }
























